d-color: @linkColor;\n			.border-radius(300px);\n			overflow: visible!important;\n			position: relative;\n			strong {\n				opacity: 0;\n				color: #fff;\n				&:after{\n					right: 13px;\n				}\n			}\n		}\n		strong {\n			position: absolute;\n			right: -0px;\n			top: -25px;\n			opacity: .2;\n			padding: 4px 7px!important;\n			background-color: #333;\n			color: rgba(255,255,255,.8);\n			line-height: 12px!important;\n			font-size: 11px!important;\n			display: block;\n			font-family: @defaultFont;\n			.border-radius(2px);\n			&:after {\n				border-color: #333 transparent;\n			    border-style: solid;\n			    border-width: 5px 5px 0;\n			    bottom: -5px;\n			    content: \"\";\n			    display: block;\n			    right: 5px;\n			    position: absolute;\n			    width: 0;\n			}\n			&.full:after {\n				left: 15px;\n			}\n		}\n		p {\n			font-size: @fontSizeSmall;\n			margin: 0;\n			padding-bottom: 2px;\n		}\n	}\n}\n\n.pp-plan{\n	text-align: center;\n	.border-radius(7px); \n	box-shadow: 0 0 10px rgba(0,0,0,.3);\n	overflow:hidden;\n	.pp-header{\n		text-align: center;\n		color: @pl-base;\n		font-weight: 600;\n		text-shadow: 0 -1px 0 rgba(0,0,0,.3);\n		.pp-title{\n			background: @linkColor;\n			font-size: 18px;\n			padding: 4px;\n		}\n		.pp-price{\n			background: darken( @linkColor, 10% );\n			padding: 20px;\n			\n			.price-pre{\n				vertical-align: top;\n				font-size: 1.2em;\n			}\n			.price{\n				font-size: 70px;\n				line-height: 70px;\n				font-weight: 800;\n				\n			}\n			.price-pre, \n			.price-post,\n			.price-sub{\n				opacity: .7;\n			}\n			.price-sub{\n				font-style: italic; \n				font-size: 12px;\n				font-weight: 400;\n				margin-top: 7px;\n				line-height: 1.5em;\n			}\n		}\n	}\n	.pp-attributes ul {\n		\n		li{\n			padding: 10px;\n			font-weight: 600;\n			border-top: 1px solid @baseContrastDark;\n			color: @textColorSubtle;\n			&.emphasis{\n				font-size: 120%;\n				color: @textColor;\n			}\n			&:nth-child( even ) { \n				background: @baseContrast;\n			}\n			&.pp-link .btn{\n				\n				.border-radius(30px);\n				padding: 7px 40px; \n				i{\n					margin-left: 10px;\n				}\n			}\n		}\n	}\n}.section-mediabox {\n	\n	.pl-section-pad{\n		padding: 0;\n	}\n	.mediabox-wrap{\n		min-height: 50px;\n	//	overflow: hidden;\n		.background-size(cover);\n		\n	}\n\n	img,\n	iframe,\n	object, \n	embed{\n		max-width: 100%;\n		max-height: 100%;\n	}\n	\n	\n	\n}/*\n * jQuery FlexSlider v1.8\n * http://www.woothemes.com/flexslider/\n *\n * Copyright 2012 WooThemes\n * Free to use under the MIT license.\n * http://www.opensource.org/licenses/mit-license.php\n */\n\n\n/* PL STYLES */\n\n.flexwrap, .fslider{\n	position: relative;\n}\n\n\n/* Browser Resets */\n.flex-container a:active,\n.flexslider a:active,\n.flex-container a:focus,\n.flexslider a:focus  {outline: none;}\n.slides,\n.flex-control-nav,\n.flex-direction-nav {margin: 0; padding: 0; list-style: none;}\n\n/* FlexSlider Necessary Styles\n*********************************/\n.flexslider {\n	width: 100%;\n	margin: 0;\n	padding: 0;\n	.slides > li {\n		display: none;\n		-webkit-backface-visibility: hidden;/* Hide the slides before the JS is loaded. Avoids image jumping */\n	}\n	.slides img {width: 100%; display: block;}\n}\n\n.flex-pauseplay span {text-transform: capitalize;}\n\n/* Clearfix for the .slides element */\n.slides:after {content: \".\"; display: block; clear: both; visibility: hidden; line-height: 0; height: 0;}\nhtml[xmlns] .slides {display: block;}\n* html .slides {height: 1%;}\n\n/* No JavaScript Fallback */\n/* If you are not using another script, such as Modernizr, make sure you\n * include js that eliminates this class on page load */\n.no-js .slides > li:first-child {display: block;}\n\n\n/* FlexSlider Default Theme\n*********************************/\n.fslider{\n	background: rgba(255,255,255,.4);\n	box-shadow: 0 1px 4px rgba(0,0,0,.4);\n	-webkit-border-radius: 5px; -moz-border-radius: 5px; -o-border-radius: 5px; border-radius: 5px;\n	padding: 5px;\n}\n.flexslider {\n	position: relative;\n	zoom: 1;\n	.slides {\n		zoom: 1;\n		> li {position: relative;}\n	}\n}\n\n/* Suggested container for \"Slide\" animation setups. Can replace this with your own, if you wish */\n.flex-container {zoom: 1; position: relative;}\n\n.flex-caption {\n	background:none;\n	zoom: 1;\n	text-align: left;\n	max-width: 42%;\n	padding: 13px 2%;\n	margin: 0;\n	position: absolute;\n	right: 0;\n	bottom: 0;\n	background: rgba(0,0,0,.4);\n	color: #fff;\n	text-shadow: 0 -1px 0 rgba(0,0,0,.6);\n	box-shadow: 1px 1px 0 rgba(255,255,255, .1), -1px -1px 0 rgba(255,255,255, .1);\n	font-size: 15px;\n	line-height: 1.5em;\n}\n\n/* Direction Nav */\n.flex-direction-nav {\n	height: 0;\n	li a{\n		text-indent:-99999px; //IE8\n		margin-top: -42px; /* 16 (half height of control nav) + 26 (half of height) */\n		width: 52px;\n		height: 52px;\n		display: block;\n		position: absolute;\n		top: 50%;\n		cursor: pointer;\n		color: transparent;\n		background-color: rgba(50,50,50,.5);\n		.border-radius(25px);\n		background-image:url(@{plSectionsRoot}/quickslider/images/bg_direction_nav.png);\n		&:hover{\n			background-color: rgba(50,50,50,.4);\n\n		}\n		&:active{\n			background-color: rgba(50,50,50,.8);\n		}\n		&.next {background-position: -52px 0; right: 15px;}\n		&.prev {left: 15px;}\n		&.disabled {\n			opacity: .3;\n			filter:alpha(opacity=30);\n			cursor: default;\n		}\n	}\n}\n.no-control-nav .flex-direction-nav li a{\n	margin-top: -26px;\n}\n\n.section-quickslider{\n	.content-pad{\n		padding-bottom: 0;\n\n	}\n	.wrap-no-control-nav{\n		padding-bottom: 10px;\n	}\n}\n\n/* Control Nav */\n.flex-control-nav {\n	width: 100%;\n	padding-top: 15px;\n	text-align: center;\n	li {\n		margin: 0 0 0 5px;\n		display: inline-block;\n		zoom: 1;\n		*display: inline;\n		&:first-child {margin: 0;}\n		a {\n			width: 13px;\n			height: 13px;\n			display: block;\n			cursor: pointer;\n			background:@baseContrastSlight;\n\n\n			.border-radius(6px);\n			cursor: pointer;\n			text-indent: -999em;\n			&.active {\n				background:@baseContrastMore;\n				cursor: default;\n			}\n		}\n\n	}\n}\n/*\n * PageLines Inc.\n */\n\nbody.navbar_fixed {\n	.page-canvas {\n		padding-top: 50px;\n	}\n	.fixed_width #page .page-canvas{\n		margin-top: 42px;\n		padding-top: 0;\n	}\n\n	.section-navbar.fixed-top{\n		position: fixed;\n		z-index: @zindexFixedNavbar;\n	}\n	\n}\n\n.section-navbar.fixed-top{\n	.navbar-edit{\n		position: absolute;\n		left: -30px;\n		top: 6px;\n		display: none;\n	}\n	&:hover{\n		.navbar-edit{\n			display: block;\n		}\n	}\n}\n\n/*  Basic formatting\n ************************************************************/\n.section-navbar {\n\n\n	margin: 0 auto;\n	width: 100%;\n	font-family: @altFontFamily;\n	font-weight: @altFontWeight;\n\n	.content-pad,\n	.pl-section-pad{\n		padding-top: 5px;\n		padding-bottom: 5px;\n		.navbar-content-pad{\n			padding: 0 15px;\n		}\n	}\n\n}\n// Dont nest, needs to be minimum specificity\n.pldrop ul{\n	display: none;\n}\n.navbar{\n	//BPG: no shadow\n	//.box-shadow(~\"inset 0 1px 0 rgba(255,255,255,.1), 0 1px 3px rgba(0, 0, 0, 0.25)\");\n	color:rgba(255,255,255,.7);\n	&.navbar-content-width{\n		.border-radius( 4px );\n	}\n	&.plnav_hover {\n		.sub-menu .dropdown-menu {\n			position: absolute;\n			top: 0;\n			left: 100%;\n			margin-top: -1px;\n			visibility: hidden;\n		}\n		.dropdown-menu li:hover .sub-menu {\n		  	visibility: visible;\n		  	margin-top:-6px;\n		}\n		.dropdown:hover .dropdown-menu {\n			margin-top:0;\n		  	display: block;\n		}\n	}\n	.navline {\n		float: left;\n		position: relative;\n		left: 0;\n		display: block;\n		margin: 0;\n		font-size: .9em;\n		padding: 14px 0 0 0;\n\n		// BPG: bigger menu text\n		> li {\n			font-size: 120%;\n		}\n		> .dropdown > .dropdown-menu{\n			&:before {\n			  content: \'\';\n			  display: inline-block;\n			  border-left: 7px solid transparent;\n			  border-right: 7px solid transparent;\n			  border-bottom: 7px solid #ccc;\n			  border-bottom-color: rgba(0, 0, 0, 0.2);\n			  position: absolute;\n			  top: -7px;\n			  left: 9px;\n			}\n			&:after {\n			  content: \'\';\n			  display: inline-block;\n			  border-left: 6px solid transparent;\n			  border-right: 6px solid transparent;\n			  border-bottom: 6px solid #ffffff;\n			  position: absolute;\n			  top: -6px;\n			  left: 10px;\n			}\n		}\n		.caret{\n			border-top-color:  rgba(255,255,255,.5);\n		}\n		&.pull-right {\n			float: right;\n			position: relative;\n			> li:last-child .dropdown-menu{\n				left: auto;\n				right: 0;\n				&:before{\n					left: auto;\n					right: 12px;\n				}\n				&:after{\n					left: auto;\n					right: 13px;\n				}\n				.dropdown-submenu > a:after {\n				  	border-width: 5px 5px 5px 0;\n					border-right-color: darken(@dropdownBackground, 20%);\n				}\n				.dropdown-menu {\n					left: -100%;\n					right: auto;\n					margin-left: 0;\n					margin-right: -1px;\n					.border-radius(6px 0 6px 6px);\n				}\n			}\n\n		}\n\n\n		> li {\n			display: block;\n			float: left;\n\n			> a {\n				text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);\n				&:hover{\n					color:rgba(255,255,255,.9);\n				}\n				float: none;\n				display: block;\n				padding: 9px 12px 9px;\n				line-height: 19px;\n				color:#fff; /* IE */\n				color:rgba(255,255,255,.8);\n				text-decoration: none;\n				text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);\n				&:hover{\n					background-color: transparent;\n					color:rgba(255,255,255,.9);\n					text-decoration: none;\n				}\n			}\n			&.dropdown.open > a{\n				color:#fff;\n				background: rgba(0, 0, 0, 0.1);\n			}\n			&.current-menu-item > a {\n			  	background-color: rgba(0,0,0,.1);\n				color:#ffffff;\n				text-decoration: none;\n			}\n			&.active > a:hover {\n				color:rgba(255,255,255,.9);\n				  text-decoration: none;\n				  background-color: #222222;\n			}\n		}\n	}\n\n	.searchform{\n		//BPG: don\'t float right\n		//float: right;\n		margin: 0;\n		width: auto;\n		padding: 21px 0 0 10%;\n		max-width: 190px;\n		.searchfield{\n                        // BPG: Don\'t start out small (inherited value here is 23px)\n              