#fff; \n	}\n	a:hover{\n		opacity: .7;\n		color:inherit;\n	}\n}\n.pl-black{\n	background:#000; \n	.pl-contrast{\n		background: #222; \n	}\n}\n\n.pl-grey{\n	background:#1c1c1c; \n	.pl-contrast{\n		background: #000; \n	}\n}\n\n.pl-dark-img{\n	background:#000; \n	color: #fff; \n	text-shadow: 0 -1px 0 rgba(0,0,0,.8);\n	.pl-contrast{\n		background: rgba(0,0,0,.2); \n	}\n	a{\n		color: #fff;\n	}\n}\n\n.pl-light-img{\n	background:#fff; \n	color: #000; \n	text-shadow: 0 1px 0 rgba(255,255,255,.8);\n	.pl-contrast{\n		background: rgba(255,255,255,.2); \n	}\n}\n\n/*\n * FIXED TOP\n ***********************************/\n.pl-fixed-top{\n	position: fixed;\n	z-index: @zindexFixedNavbar;\n	left: 0;\n	width: 100%;\n}\n.fixed-top-pusher{\n	.transition(all 0.1s linear);\n}\n\n@media (max-width: @resPortraitTablet) {\n\n	.pl-fixed-top{\n		position: static;\n	}\n	.fixed-top-pusher{\n		display: none;\n	}\n\n}\n\n\n/*\n * RESPONSIVE: GLOBAL\n ****************************************/\nbody{\n\n	// Images\n	.pl-content img {\n		max-width: 100%;\n		height: auto;\n		width: auto\\9; /* ie8 */\n	}\n	.ie8 img {\n		width: auto;\n		height: auto;\n	}\n\n	// Media\n	.hentry{\n		iframe,\n		video,\n		object{\n			max-width: 100%;\n		}\n	}\n\n}\n\n/*\n * RESPONSIVE: TABLET\n ****************************************/\n@media screen and ( max-width:@resPortraitTablet ){\n\n	.site-wrap{\n		-webkit-backface-visibility: hidden; // fixes flicker\n	}\n\n}\n\n/*\n * RESPONSIVE: PHONE\n ****************************************/\n@media screen and ( max-width : @resPhoneLandscape ) {\n	\n	.responsive{\n\n	}\n\n}\n\n\n\n\n\n@placeholderBG: #f1fcfd;\n\n@interfaceBG: rgba(0,0,0,.6);\n\n@editorBorder: #35eafd;\n@editorBorderTrans: rgba(53, 234, 253, .5);\n@editorText: #038196;\n\n.pl-editor{\n	\n	\n\n}\n\n// Drag and Drop Editing\n// --------------------------------------------------\n\n// Used to disable text selection, used in resize.. and more\n.disable-select{\n	.user-select(none);\n}\n\n.pl-section-controls,\n.pl-region-bar,\n.pl-area-controls,\n.pl-column-forcer,\n.pl-sortable-buffer, \n.li-refresh, \n.pl-editor-only{\n	display: none;\n}\n\n.btn-region,\n.pl-section-controls{\n	font-family: @defaultFont;\n}\n\n.drag-drop-editing{\n\n	// Area Controls\n	// --------------------------------------------------\n\n	.pl-editor-only{\n		display: block;\n	}\n	.pl-region{\n		position: relative;	\n	}\n	.pl-region-bar{\n		display: block;\n	\n		z-index: @zindexEditor;\n		#gradient > .vertical(rgba(182, 242, 253, .1), rgba(125, 233, 253, .1));\n		border-top: 1px dashed @editorBorder;\n		width: 100%;\n		height: 11px;\n		line-height: 10px;\n		a.btn-region{\n			cursor: help;\n			#gradient > .vertical(rgba(182, 242, 253, .6), rgba(125, 233, 253, .6));\n			color: rgba(0,0,0,.6);\n			text-shadow: 0 1px 0 rgba(255,255,255,.4);\n			box-shadow: inset 2px 1px 0 rgba(255,255,255,.5);\n			border-right: 1px dashed @editorBorder;\n			font-size: 8px;\n			line-height: 13px;\n			padding: 0px 4px;\n			font-weight: bold;\n			vertical-align: middle;\n		}\n	}\n\n	.pl-area-container{\n		min-height: 20px;\n	}\n	.pl-area-placeholder{\n		max-height: 400px;\n		border-top: 1px dashed @editorBorder;\n		#gradient > .vertical(rgba(182, 242, 253, .06), rgba(125, 233, 253, .06));\n	}\n\n	.pl-area{\n		position: relative;\n		border-top: 1px dashed @editorBorder;\n		&.ui-sortable-helper{\n			border: 1px dashed @editorBorder;\n			.pl-area-controls{\n				display: none;\n			}\n		}\n		.pl-area-controls{\n			.transition(all 0.15s ease-in-out);\n			display: block;\n			z-index: @zindexEditor;\n			position: absolute;\n			left: 0;\n			top: 4px;\n			text-align: right;\n			width: 72px;\n			line-height: 1em;\n			font-size: 9px;\n\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			margin-left: -54px;\n\n			\n			&:hover{\n				opacity: 1;\n				margin-left: 0;\n				z-index: @zindexTooltip;\n			}\n			&::after{\n							content: \'\';\n							display: block;\n							width: 200px;\n							height: 160%;\n							position: absolute;\n							top: -5%;\n							right: -50px;\n							z-index: -100;\n						}\n\n\n			.area-control{\n				display: inline-block;\n				font-size: 10px;\n				font-weight: bold;\n				text-shadow: 0 -1px 0 @interfaceBG;\n				color: #fff;\n				height: 17px;\n				width: 17px;\n				line-height: 17px;\n				text-align: center;\n				&.area-reorder:hover{\n					cursor: move;\n				}\n				&:hover{\n					\n					cursor:pointer;\n					background:rgba(0,0,0,.6);\n				}\n				&:active{\n					background:rgba(0,0,0,1);\n				}\n			}\n		}\n	}\n\n	&.display-boxed .pl-area .pl-area-controls{\n		opacity: .5;\n		margin-left: -70px;\n	\n		&:hover{\n			opacity: 1;\n			margin-left: -70px;\n		}\n	}\n\n	.pl-section{\n		.box-sizing(border-box);\n		position: relative;\n		.transition(all 0.1s linear);\n		&:hover,\n		&.ui-sortable-helper {\n\n			cursor: move;\n			background: fadeout(@baseContrast, 60%);\n			> .pl-section-controls{\n				display: block;\n			}\n		}\n		&.ui-sortable-helper{\n			.transition(none);\n		}\n\n\n		&.section-plcolumn{\n			&:hover{background: transparent;}\n			margin-bottom: 20px;\n			outline: 1px dashed #35eafd;\n\n			&.empty-area .pl-sortable-column{\n				min-height: 70px;\n				.pl-column-forcer{\n					display: block;\n					width: 0;\n					height: 0;\n				}\n\n			}\n			&.force-start-row > .pl-section-controls:first-child a.s-control.section-start-row{\n				background:@editorBorder;\n			}\n			> .pl-section-controls:first-child{\n				top: -20px;\n				#gradient > .vertical(rgba(182, 242, 253, .8), rgba(125, 233, 253, .8));\n				&:hover{\n					#gradient > .vertical(rgba(182, 242, 253, .9), rgba(125, 233, 253, .9));\n				}\n				box-shadow: inset 1px 1px 0 rgba(255,255,255,.5);\n				outline: 1px dashed  @editorBorder;\n				color: @editorText;\n				text-shadow: 0 1px 0 rgba(255,255,255,.4);\n\n				a.s-control{\n					color: @editorText;\n					background: rgba(196, 235, 242, .4);\n					&:hover{\n						background:@editorBorder;\n						color:#000;\n					}\n					&:active{\n						background-color: #01c5df;\n						color:#222;\n					}\n				}\n\n			}\n\n		}\n		// Section Controls\n		.pl-section-controls{\n			position: absolute;\n			top: 0;\n			left: 0;\n			width: 100%;\n			z-index: @zindexEditor;\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			text-align: center;\n			\n			font-weight: bold;\n			font-size: 10px;\n			line-height: 21px;\n			.box-sizing(border-box);\n			&:hover{\n				cursor: move;\n			}\n			.controls-title,\n			.controls-right,\n			.controls-left{\n				display: inline;\n				clear: none;\n			}\n			.title-desc{\n				color: rgba(255,255,255, .5);\n			}\n			.controls-right{\n				padding-right: 4px;\n				float: right;\n			}\n			.controls-left{\n				padding-left: 4px;\n				float: left;\n			}\n			.s-control{\n				line-height: 16px;\n\n				font-size: 11px;\n				display: inline-block;\n				text-align: center;\n			}\n			span.s-control{\n				letter-spacing: .1em;\n			}\n			a.s-control{\n\n				width: 16px;\n				height: 16px;\n\n				padding: 0 1px;\n\n				.border-radius(3px);\n				background: rgba(0,0,0,.3);\n				&:hover{\n					background: rgba(0,0,0,.6);\n					color:#eee;\n				}\n				&:active{\n					background: rgba(0,0,0,.4);\n					color:#ccc;\n				}\n				.caret, &:hover .caret{\n				  border-top:   4px solid #fff;\n				  .opacity(60);\n				  content: \"\";\n				}\n				&.s-control-icon{\n					width: 16px;\n					height: 16px;\n					color:#fff;\n				}\n			}\n\n\n		}\n\n	}\n\n	.pl-sortable-area,\n	.pl-sortable-column{\n		min-height: 35px;\n		.transition(.5s);\n	}\n	&.pl-dragging{\n\n		.pl-sortable-column{\n			padding-top: 0px;\n		}\n		.section-plcolumn{\n			background: @pl-base;\n			outline: 1px dashed #35eafd;\n		}\n		.ui-sortable-helper{\n			border: 1px solid rgba(255,255,255,.4);\n			box-shadow: 0 1px 4px rgba(0,0,0,.5);\n			max-height: 350px;\n			overflow:hidden;\n			opacity: .5;\n		}\n		.pl-sortable-buffer{\n			height: 5px;\n			margin: 5px 0;\n			display: block;\n		}\n		&:hover,\n		&.ui-sortable-helper,\n		.section-plcolumn {\n			.pl-section-controls{\n				display: none !important;\n			}\n		}\n\n	}\n\n\n	// Area for dropping elements on drag\n	.pl-placeholder {\n		max-height: 200px;\n		display: inline;\n		float: left;\n		clear: none;\n		position: relative;\n		margin: 0 0 1em 0;\n		padding: 0;\n		.box-sizing(border-box);\n		.border-radius(5px);\n		#gradient > .vertical(rgba(182, 242, 253, .2), rgba(125, 233, 253, .2));\n		outline: 1px solid @editorBorderTrans;\n		// Used in nested columns\n		&.hidden-placeholder{\n			display: none;\n		}\n	}\n	.pl-sortable-area,\n	.pl-sortable-column{\n		.empty-area{\n			min-height: 35px;\n		}\n	}\n\n\n\n	.cant-offset .section-offset-increase,\n	.cant-offset .section-offset-reduce,\n	.cant-offset .offset-size,\n	.sortable-first .section-start-row{\n		.opacity(50);\n	}\n\n	.force-start-row .s-control.s-control-icon.section-start-row{\n		.opacity(100);\n		color: @editorBorder;\n		background: rgba(0,0,0,.7);\n	}\n\n\n\n	.loaded-notice{\n		.the-notice{\n			background:rgba(0,0,0,.6); \n			color:#fff; \n			display: inline-block; \n			padding: 5px 30px;\n			margin: 10px 0;\n			font-size: 11px;\n		}\n		text-align: center;\n		font-family: @buttonFontFamily;\n		font-weight: bold;\n	}\n\n\n}\n\n\n\n// Layout\n// --------------------------------------------------\n.width-resize{\n	// Resizing\n	.ui-resizable-handle {\n		&.ui-resizable-e,\n		&.ui-resizable-w{\n			cursor: move;\n			position: absolute;\n			font-size: 0.1px;\n			display: block;\n		}\n\n\n	}\n\n	&.resize-hover .ui-resizable-handle{\n		background: rgba(182, 242, 253, .2);\n\n	}\n	&.width-resizing .ui-resizable-handle{\n		background: rgba(182, 242, 253, .4);\n	}\n\n\n\n\n	.ui-resizable-e{\n		cursor: e-resize;\n		border-left: 1px dashed @editorBorder;\n		width: 5px;\n		right: -6px;\n		top: 0;\n		height: 100%;\n	}\n\n	.ui-resizable-w{\n		cursor: w-resize;\n		border-right: 1px dashed @editorBorder;\n		width: 5px;\n		left: -6px;\n		top: 0;\n		height: 100%;\n	}\n\n	&.width-resize-edge{\n		.ui-resizable-e{\n\n			border-left: 1px dashed @editorBorder;\n\n			right: 0;\n		}\n		.ui-resizable-w{\n\n			border-right: 1px dashed @editorBorder;\n\n			left: 0;\n\n		}\n	}\n\n	.footer .pl-area:last-child{\n		border-bottom: 1px dashed @editorBorder;\n	}\n	\n	.pl-content.nested-section-area{\n		border-top: 1px dashed @editorBorder;\n		border-bottom: 1px dashed @editorBorder;\n	}\n\n}\n\n\n// Need to refresh the page banner\n// --------------------------------------------------\n.pl-refresh-banner{\n	display: block;\n	padding: 20px;\n	text-align: center;\n	font-weight: bold;\n	opacity: .7;\n	.banner-content{\n		width: 130px;\n		margin: 0 auto;\n		.btn,\n		.banner-frame{\n			box-shadow: inset 1px 1px 0 rgba(255,255,255,.2), 1px 1px 0 rgba(255,255,255,.4);\n			border:1px solid rgba(0,0,0,.3);\n		}\n		.banner-frame{\n			max-width: 120px;\n			margin-bottom: 8px;\n			display: inline-block;\n			img{\n				max-width: 100%;\n			}\n		}\n	}\n	\n}\n\n\n.pl-credit,\n.toolbox-activate{\n	\n	position: fixed; \n	bottom: 0; \n	background: rgba(0,0,0,.9); \n	font-family: @buttonFontFamily;\n	font-weight: 300;\n	color: #fff; \n	text-shadow: 0 -1px 0 #000;\n	opacity: .6;\n	padding: 0 8px 0px 10px;\n	font-size: 15px;\n	height: 29px; \n	line-height: 29px;\n	text-align: center;\n	.transition(all .5s ease);\n	cursor: pointer;\n	.transit{\n		.transition(all .5s ease);\n	}\n	.fademein{\n		opacity: 0;\n		.transition(all .7s ease);\n	}\n	&:hover{\n		.fademein{\n			opacity: 1;\n		}\n		\n		color: #fff;\n		opacity: 1;\n		i{\n			color: #35eafd;\n			text-shadow: -1px 1px 8px rgba(53, 254, 253, .4), 1px -1px 8px rgba(53, 254, 253, .4);\n		}\n		\n	}\n}\n\n.pl-credit{\n	.border-top-left-radius(3px);\n	right: -33px; \n	box-shadow: -1px -1px 0 rgba(255,255,255,.2);\n	display: block !important;\n	visibility: visible !important;\n	opacity: 1 !important;\n	&:hover{\n		right:0;\n	}\n	&::after{\n		content: \'\';\n		display: block;\n		width: 200px;\n		height: 160%;\n		position: absolute;\n		top: -20px;\n		right: 0;\n		z-index: -100;\n	}\n}\n.toolbox-activate{\n	.border-top-right-radius(3px);\n	left: 0;\n	box-shadow: 1px -1px 0 rgba(255,255,255,.2);\n}\n\n\n// Toolbox/Toolbar\n// --------------------------------------------------\n\n#PageLinesToolbox.pl-toolbox{\n	position: fixed;\n	bottom: 0;\n	color: #000;\n	background: #fff;\n	font-size: 13px;\n	line-height: 21px;\n	width: 100%;\n	z-index: @zindexEditor;\n	min-width: 760px;\n	font-family: @defaultFont;\n	h1, h2, h3, h4, h5, h6{\n		font-family: @defaultFont;\n		font-weight: normal;\n	}\n	font-weight: normal;\n	select, textarea, input{\n		font-size: 14px;\n		line-height: 1.5em;\n	}\n	button{font-weight: bold;}\n	.type-textarea{\n		font-size: 12px;\n		line-height: 1.5em;\n		min-height: 9em;\n		padding: .7em;\n	}\n	.resizer-handle{\n		position: absolute;\n		height: 4px;\n		top: -4px;\n		width: 100%;\n		cursor: row-resize;\n		&:hover,\n		&.resizing{\n			background: rgba(53, 234, 253, .5);\n		}\n	}\n	.toolbox-handle{\n\n		@media (max-width: 1164px) {\n			 .btn-toolbox .txt{\n					display: none;\n			}\n		}\n\n		text-align: left;\n		.buttonBackground(#eee, #ddd);\n		box-shadow: inset 1px 1px 0 rgba(255,255,255,.8), 0 1px 3px rgba(0,0,0,.2);\n		border-top: 1px solid #999;\n		border-bottom: 1px solid #999;\n		font-weight: bold;\n		text-shadow: 0 1px 0 #fff;\n		height: 28px;\n		line-height: 28px;\n		font-size: 13px;\n		padding: 0 20px 0 24px;\n\n		.user-select(none);\n\n\n		ul.controls{\n			float: left;\n			margin-left: 11px;\n			border-left: 1px solid #AAA;\n			border-right: 1px solid #fff;\n			position: relative;\n			&.not-btn{\n				border-left: 1px solid transparent;\n				border-right: 1px solid transparent;\n				overflow: hidden;\n\n				width: 150px;\n				height: 28px;\n				> li {\n					&.switch-btn{\n						right: 0;\n						top: -30px;\n						opacity: 0;\n						position: absolute;\n						.transition( ~\"top .2s, opacity .3s\" );\n						&.active{\n							top: 0;\n							opacity: 1;\n						}\n						> .btn-to